// n8n Code node (JavaScript)

function findFields(root) {
  if (Array.isArray(root)) {
    for (const el of root) {
      const got = findFields(el);
      if (got) return got;
    }
    return null;
  }
  if (Array.isArray(root?.body?.data?.fields)) return root.body.data.fields;
  if (Array.isArray(root?.data?.fields)) return root.data.fields;
  if (Array.isArray(root?.fields)) return root.fields;

  if (Array.isArray(root?.data)) {
    for (const el of root.data) {
      const got = findFields(el);
      if (got) return got;
    }
  }

  for (const k of Object.keys(root || {})) {
    const v = root[k];
    if (Array.isArray(v)) {
      for (const el of v) {
        const got = findFields(el);
        if (got) return got;
      }
    }
  }
  return null;
}

function extractSelectedTexts(fields) {
  // Works for MULTI_SELECT or CHECKBOXES with options[] + value[]
  const master = fields.find(
    f => Array.isArray(f?.options) && Array.isArray(f?.value)
  );

  if (master) {
    const idToText = new Map(master.options.map(o => [o.id, o.text]));
    return (master.value || []).map(id => idToText.get(id)).filter(Boolean);
  }

  // Fallback for per-option boolean CHECKBOX rows (not expected in this payload)
  const extras = fields
    .filter(f => f?.type === 'CHECKBOXES' && f?.value === true)
    .map(f => {
      const m = /\((.+)\)\s*$/.exec(f.label || '');
      return m ? m[1] : null;
    })
    .filter(Boolean);

  return extras;
}

return items.map(item => {
  const fields = findFields(item.json ?? {}) || [];
  const texts = extractSelectedTexts(fields);

  const out = {};
  texts.forEach((t, i) => { out[`slot_${i + 1}`] = t; });

  return { json: out };
});

// For each incoming item, take item.json.output (an array)
// and return one item per entry: { json: { firstname, phone } }

const results = [];

for (const item of items) {
  const arr = item.json?.output ?? [];
  if (!Array.isArray(arr)) continue;

  for (const entry of arr) {
    results.push({ json: entry });
  }
}

return results;
